mapper.select:
select e.emp_no ,e.emp_nm ,e.emp_status ,(select c.cmm_nm from cmm_code c where c.cmm_class='EMP_STATUS' and c.cmm_cd = e.emp_status) as emp_status_nm ,e.dept_cd ,(select d.dept_nm from department d where d.dept_cd = e.dept_cd) as dept_nm ,e.position_cd ,(select c.cmm_nm from cmm_code c where c.cmm_class = 'POSITION_CD' and c.cmm_cd = e.position_cd) as position_nm ,e.hire_date ,e.gender ,(select c.cmm_nm from cmm_code c where c.cmm_class='GENDER' and c.cmm_cd = e.gender) as gender_nm ,e.birth ,e.email from employee e
where e.emp_nm like #{keyword}
where e.emp_no like #{keyword}
order by hire_date

select count(*) from employee

	where emp_nm like #{keyword}


	where emp_no like #{keyword}
